package app.bash.data;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;import app.bash.data.data_model.CardModel;import app.bash.data.data_model.MyCartModel;import app.bash.data.data_model.UserDataModel;public class DataManager extends SQLiteOpenHelper {    public static final int DATABASE_VERSION = 3;    public static final String DATABASE_NAME = "BashApp";    public DataManager(Context context, String name, SQLiteDatabase.CursorFactory factory, int version) {        super(context, name, factory, version);    }    @Override    public void onCreate(SQLiteDatabase db) {        UserDataModel.creteTable(db);        MyCartModel.creteTable(db);        CardModel.creteTable(db);       /* StudentDataModel.creteTable(db);        StudentMarksDataModel.creteTable(db);*/    }    @Override    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {        UserDataModel.dropTable(db);        MyCartModel.dropTable(db);        CardModel.dropTable(db);       /* StudentDataModel.dropTable(db);        StudentMarksDataModel.dropTable(db);*/        onCreate(db);    }}